import React, { useState, useEffect } from "react";
import {
  Box,
  Button,
  Typography,
  TextField,
  Select,
  MenuItem,
  FormHelperText,
  FormControl,
  InputLabel,
  Paper,
  Autocomplete,
  CircularProgress,
  Chip,
  Modal,
  InputAdornment,
  IconButton,
  Menu,
} from "@mui/material";
import { Visibility, VisibilityOff, Settings } from "@mui/icons-material";
import { SelectChangeEvent } from "@mui/material/Select";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import { API_ENDPOINTS } from "../../config/api";
import Header from "../../components/Header";

interface DiagnosticoCIE10 {
  codigo: string;
  descripcion: string;
}

export default function SolicitarLicencia() {
  const [form, setForm] = useState({
    nombre: "",
    apellido: "",
    documento: "",
    area: "",
    motivo: "",
    observaciones: "",
    fechaInicio: "",
    fechaFin: "",
    archivo: null as File | null,
    diagnosticoCIE10: null as DiagnosticoCIE10 | null,
  });

  const [errors, setErrors] = useState({
    nombre: false,
    apellido: false,
    documento: false,
    area: false,
    motivo: false,
    fechaInicio: false,
    fechaFin: false,
    archivo: false,
    diagnosticoCIE10: false,
  });

  // Estados para el buscador CIE-10
  const [cie10Search, setCie10Search] = useState("");
  const [cie10Results, setCie10Results] = useState<DiagnosticoCIE10[]>([]);
  const [cie10Loading, setCie10Loading] = useState(false);

  // Estados para el modal de cambiar contraseña
  const [modalOpen, setModalOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState<string | null>(null);
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showOld, setShowOld] = useState(false);
  const [showNew, setShowNew] = useState(false);

  // Estados para el menú de usuario
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [userName, setUserName] = useState<string>("");

  useEffect(() => {
    // Obtener el nombre del usuario desde localStorage
    const name =
      localStorage.getItem("username") ||
      localStorage.getItem("nombre") ||
      "Usuario";
    setUserName(name);
  }, []);

  // Función para buscar diagnósticos CIE-10
  const buscarDiagnosticosCIE10 = async (query: string) => {
    if (query.length < 3) {
      setCie10Results([]);
      return;
    }

    setCie10Loading(true);
    try {
      const response = await fetch(
        `${API_ENDPOINTS.CIE10_SEARCH}?query=${encodeURIComponent(query)}`
      );
      if (response.ok) {
        const data = await response.json();
        setCie10Results(data);
      } else {
        setCie10Results([]);
      }
    } catch (error) {
      setCie10Results([]);
    } finally {
      setCie10Loading(false);
    }
  };

  // Retarda la ejecución de la búsqueda mientras el usuario escribe
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (cie10Search) {
        buscarDiagnosticosCIE10(cie10Search);
      }
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [cie10Search]);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
    if (value.trim() !== "") setErrors((prev) => ({ ...prev, [name]: false }));
  };

  const handleSelectChange = (e: SelectChangeEvent<string>) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
    if (value !== "") setErrors((prev) => ({ ...prev, [name]: false }));
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    if (file && file.type !== "application/pdf") {
      setErrors((prev) => ({ ...prev, archivo: true }));
      setForm((prev) => ({ ...prev, archivo: null }));
    } else {
      setForm((prev) => ({ ...prev, archivo: file }));
      setErrors((prev) => ({ ...prev, archivo: false }));
    }
  };

  // Cambiar contraseña desde el modal
  const handleChangePassword = async () => {
    setLoading(true);
    setMsg(null);
    try {
      const res = await fetch(
        "http://localhost:4000/api/usuario/auth/cambiar-password",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username:
              localStorage.getItem("username") ||
              localStorage.getItem("nombre") ||
              "",
            oldPassword,
            newPassword,
          }),
        }
      );
      const data = await res.json();
      if (res.ok) {
        setMsg("Contraseña cambiada correctamente");
        setTimeout(() => setModalOpen(false), 1200);
      } else {
        setMsg(data.error || data.message || "Error al cambiar la contraseña");
      }
    } catch {
      setMsg("Error de conexión");
    } finally {
      setLoading(false);
      setOldPassword("");
      setNewPassword("");
    }
  };

  // Handlers para el menú
  const handleMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
  };
  const handleOpenModal = () => {
    setModalOpen(true);
    setAnchorEl(null);
  };
  const handleCloseModal = () => {
    setModalOpen(false);
    setOldPassword("");
    setNewPassword("");
    setMsg(null);
  };
  const handleCerrarSesion = () => {
    localStorage.clear();
    window.location.href = "/empleados";
  };

  const handleSubmit = async () => {
    const newErrors = {
      nombre: form.nombre.trim() === "",
      apellido: form.apellido.trim() === "",
      documento: form.documento.trim() === "",
      area: form.area === "",
      motivo: form.motivo === "",
      fechaInicio: form.fechaInicio === "",
      fechaFin: form.fechaFin === "",
      archivo: form.motivo === "Enfermedad" && !form.archivo,
      diagnosticoCIE10: form.motivo === "Enfermedad" && !form.diagnosticoCIE10,
    };
    setErrors(newErrors);

    // Validar que fechaFin sea posterior a fechaInicio
    if (
      form.fechaInicio &&
      form.fechaFin &&
      form.fechaFin <= form.fechaInicio
    ) {
      alert("La fecha de fin debe ser posterior a la fecha de inicio");
      return;
    }

    const hasErrors = Object.values(newErrors).some((error) => error);
    if (!hasErrors) {
      try {
        const token = localStorage.getItem("token");
        console.log("Token:", token);

        const formData = new FormData();

        // AGREGAR LOGS PARA DEBUG
        console.log("=== DATOS A ENVIAR ===");
        console.log("nombre:", form.nombre);
        console.log("apellido:", form.apellido);
        console.log("documento:", form.documento);
        console.log("area:", form.area);
        console.log("motivo:", form.motivo);
        console.log("fechaInicio:", form.fechaInicio);
        console.log("fechaFin:", form.fechaFin);
        console.log("observaciones:", form.observaciones);
        console.log("diagnosticoCIE10:", form.diagnosticoCIE10);
        console.log("archivo:", form.archivo);
        console.log("======================");

        // Agregar campos del formulario
        formData.append("nombre", form.nombre);
        formData.append("apellido", form.apellido);
        formData.append("documento", form.documento);
        formData.append("area", form.area);
        formData.append("motivo", form.motivo);
        formData.append("fechaInicio", form.fechaInicio);
        formData.append("fechaFin", form.fechaFin);
        formData.append("observaciones", form.observaciones);

        // Agregar archivo si existe
        if (form.archivo) {
          formData.append("certificadoMedico", form.archivo);
        }

        // Agregar diagnóstico CIE-10 si existe
        if (form.diagnosticoCIE10) {
          formData.append(
            "diagnosticoCIE10_codigo",
            form.diagnosticoCIE10.codigo
          );
          formData.append(
            "diagnosticoCIE10_descripcion",
            form.diagnosticoCIE10.descripcion
          );
        }

        const response = await fetch(
          "http://localhost:4000/api/licencias/solicitar",
          {
            method: "POST",
            headers: {
              Authorization: `Bearer ${token}`,
            },
            body: formData,
          }
        );

        console.log("Response status:", response.status);
        const responseText = await response.text();
        console.log("Response:", responseText);

        if (response.ok) {
          alert("Solicitud enviada correctamente");
          // Limpiar formulario
          setForm({
            nombre: "",
            apellido: "",
            documento: "",
            area: "",
            motivo: "",
            observaciones: "",
            fechaInicio: "",
            fechaFin: "",
            archivo: null,
            diagnosticoCIE10: null,
          });
        } else {
          const errorData = JSON.parse(responseText);
          alert(`Error: ${errorData.message}`);
        }
      } catch (error) {
        console.error("Error enviando solicitud:", error);
        alert("Error enviando la solicitud");
      }
    }
  };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: "url('/fondo.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        flexDirection: "column",
        overflowX: "hidden",
      }}
    >
      <Header />
       {/* Menú Editar y Cerrar Sesión */}
      <Box
        sx={{
          position: "absolute",
          top: 35,
          right: 32,
          display: "flex",
          alignItems: "center",
          zIndex: 10,
        }}
      >
        <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-end", mr: 1 }}>
          <Typography
            sx={{
              fontWeight: 400,
              letterSpacing: 2,
              fontFamily: "Tektur, sans-serif",
              fontSize: 16,
              color: "#333",
              lineHeight: 1.1,
            }}
          >
            Bienvenido/a
          </Typography>
          <Typography
            sx={{
              fontWeight: 600,
              letterSpacing: 2,
              fontFamily: "Tektur, sans-serif",
              fontSize: 18,
              color: "#1976d2",
              lineHeight: 1.1,
            }}
          >
            {userName}
          </Typography>
        </Box>
        <IconButton onClick={handleMenuOpen}>
          <Settings sx={{ fontSize: 40 }} />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          <MenuItem onClick={handleOpenModal}>Cambiar Contraseña</MenuItem>
          <MenuItem onClick={handleCerrarSesion}>Cerrar Sesión</MenuItem>
        </Menu>
      </Box>

      {/* Botón para abrir el modal de cambiar contraseña */}
      <Box sx={{ display: "flex", justifyContent: "flex-end", mt: 2, px: 4 }}>
        <Button
          component={Link}
          to="/empleados"
          variant="outlined"
          sx={{
            backgroundColor: "#1565C0",
            color: "#ffffff",
            width: 180,
            letterSpacing: 3,
            fontSize: 20,
            borderRadius: 3,
            fontFamily: "Tektur, sans-serif",
            fontWeight: 500,
            textTransform: "none",
            ml: 2,
          }}
        >
          Volver
        </Button>
      </Box>

      {/* Contenedor formulario */}
      <Box
        sx={{
          flexGrow: 1,
          px: 4,
          mt: 3,
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            width: "100%",
            maxWidth: "1100px",
            p: { xs: 3, sm: 5 },
            borderRadius: 3,
            backgroundColor: "#fff",
          }}
        >
          <Typography
            variant="h4"
            sx={{
              mb: 4,
              fontFamily: "Tektur, sans-serif",
              fontWeight: 600,
              textAlign: "center",
              color: "#333",
            }}
          >
            Solicitar Licencia
          </Typography>

          <Box
            sx={{
              display: "flex",
              flexWrap: "wrap",
              gap: 3,
              justifyContent: "space-between",
            }}
          >
            <TextField
              label="Nombre"
              name="nombre"
              value={form.nombre}
              onChange={handleInputChange}
              error={errors.nombre}
              helperText={errors.nombre && "Campo obligatorio"}
              sx={{ flex: "1 1 30%" }}
            />
            <TextField
              label="Apellido"
              name="apellido"
              value={form.apellido}
              onChange={handleInputChange}
              error={errors.apellido}
              helperText={errors.apellido && "Campo obligatorio"}
              sx={{ flex: "1 1 30%" }}
            />
            <TextField
              label="Documento"
              name="documento"
              value={form.documento}
              onChange={handleInputChange}
              error={errors.documento}
              helperText={errors.documento && "Campo obligatorio"}
              sx={{ flex: "1 1 30%" }}
              inputProps={{ maxLength: 15 }}
            />
            <FormControl fullWidth error={errors.area} sx={{ flex: "1 1 45%" }}>
              <InputLabel>Área</InputLabel>
              <Select
                name="area"
                value={form.area}
                onChange={handleSelectChange}
              >
                <MenuItem value="">Seleccione Área</MenuItem>
                <MenuItem value="Administración">Administración</MenuItem>
                <MenuItem value="Ventas">Ventas</MenuItem>
                <MenuItem value="Producción">Producción</MenuItem>
              </Select>
              {errors.area && (
                <FormHelperText>Seleccione un área</FormHelperText>
              )}
            </FormControl>

            <FormControl
              fullWidth
              error={errors.motivo}
              sx={{ flex: "1 1 45%" }}
            >
              <InputLabel>Motivo</InputLabel>
              <Select
                name="motivo"
                value={form.motivo}
                onChange={handleSelectChange}
              >
                <MenuItem value="">Seleccione Motivo</MenuItem>
                <MenuItem value="Enfermedad">Enfermedad</MenuItem>
                <MenuItem value="Vacaciones">Vacaciones</MenuItem>
                <MenuItem value="Personal">Personal</MenuItem>
              </Select>
              {errors.motivo && (
                <FormHelperText>Seleccione un motivo</FormHelperText>
              )}
            </FormControl>

            <TextField
              label="Fecha de Inicio"
              name="fechaInicio"
              type="date"
              value={form.fechaInicio}
              onChange={handleInputChange}
              error={errors.fechaInicio}
              helperText={errors.fechaInicio && "Campo obligatorio"}
              sx={{ flex: "1 1 45%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />
            <TextField
              label="Fecha de Fin"
              name="fechaFin"
              type="date"
              value={form.fechaFin}
              onChange={handleInputChange}
              error={errors.fechaFin}
              helperText={errors.fechaFin && "Campo obligatorio"}
              sx={{ flex: "1 1 45%" }}
              InputLabelProps={{
                shrink: true,
              }}
            />

            {/* Buscador CIE-10 - Solo visible cuando el motivo es Enfermedad */}
            {form.motivo === "Enfermedad" && (
              <Box sx={{ flex: "1 1 100%", mt: 2 }}>
                <Typography
                  variant="h6"
                  sx={{
                    mb: 2,
                    fontFamily: "Tektur, sans-serif",
                    fontWeight: 600,
                    color: "#333",
                  }}
                >
                  Diagnóstico CIE-10
                </Typography>

                <Autocomplete
                  options={cie10Results}
                  getOptionLabel={(option) =>
                    `${option.codigo} - ${option.descripcion}`
                  }
                  value={form.diagnosticoCIE10}
                  onChange={(_, newValue) => {
                    setForm((prev) => ({
                      ...prev,
                      diagnosticoCIE10: newValue,
                    }));
                    if (newValue) {
                      setErrors((prev) => ({
                        ...prev,
                        diagnosticoCIE10: false,
                      }));
                    }
                  }}
                  onInputChange={(_, newInputValue) => {
                    setCie10Search(newInputValue);
                  }}
                  loading={cie10Loading}
                  loadingText="Buscando diagnósticos..."
                  noOptionsText="No se encontraron diagnósticos"
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Buscar diagnóstico CIE-10"
                      placeholder="Ej: A15 o tuberculosis"
                      error={errors.diagnosticoCIE10}
                      helperText={
                        errors.diagnosticoCIE10
                          ? "Debe seleccionar un diagnóstico CIE-10"
                          : "Escriba al menos 3 caracteres para buscar"
                      }
                      InputProps={{
                        ...params.InputProps,
                        endAdornment: (
                          <>
                            {cie10Loading ? (
                              <CircularProgress color="inherit" size={20} />
                            ) : null}
                            {params.InputProps.endAdornment}
                          </>
                        ),
                      }}
                    />
                  )}
                  renderOption={(props, option) => {
                    const { key, ...otherProps } = props;
                    return (
                      <Box key={key} component="li" {...otherProps}>
                        <Box>
                          <Typography variant="body2" sx={{ fontWeight: 600 }}>
                            {option.codigo}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            {option.descripcion}
                          </Typography>
                        </Box>
                      </Box>
                    );
                  }}
                />

                {/* Mostrar diagnóstico seleccionado */}
                {form.diagnosticoCIE10 && (
                  <Box sx={{ mt: 2 }}>
                    <Chip
                      label={`${form.diagnosticoCIE10.codigo} - ${form.diagnosticoCIE10.descripcion}`}
                      onDelete={() => {
                        setForm((prev) => ({
                          ...prev,
                          diagnosticoCIE10: null,
                        }));
                      }}
                      color="primary"
                      variant="outlined"
                      sx={{
                        fontFamily: "Tektur, sans-serif",
                        fontWeight: 500,
                      }}
                    />
                  </Box>
                )}
              </Box>
            )}

            {/* Botón para subir PDF */}
            <Button
              component="label"
              variant="outlined"
              sx={{
                mt: 1,
                py: 1.5,
                width: "fit-content",
                borderRadius: 2,
                fontFamily: "Tektur, sans-serif",
                fontWeight: 600,
                letterSpacing: 1.5,
                textTransform: "none",
                backgroundColor: "#e3f2fd",
                color: "#1976d2",
                "&:hover": { backgroundColor: "#bbdefb" },
              }}
            >
              {form.archivo
                ? `Archivo: ${form.archivo.name}`
                : "Adjuntar Certificado Médico (PDF)"}
              <input
                type="file"
                hidden
                accept="application/pdf"
                onChange={handleFileChange}
              />
            </Button>
            {errors.archivo && (
              <FormHelperText error>
                Debe subir un certificado en PDF para enfermedad
              </FormHelperText>
            )}

            <TextField
              label="Observaciones"
              name="observaciones"
              value={form.observaciones}
              onChange={handleInputChange}
              fullWidth
              multiline
              rows={4}
              sx={{ flex: "1 1 100%" }}
            />

            <Button
              variant="contained"
              onClick={handleSubmit}
              sx={{
                mt: 3,
                py: 1.5,
                width: "100%",
                fontFamily: "Tektur, sans-serif",
                fontWeight: 600,
                fontSize: "1.1rem",
                borderRadius: 2,
                letterSpacing: 2,
                backgroundColor: "#1976d2",
                "&:hover": { backgroundColor: "#115293" },
              }}
            >
              Enviar Solicitud
            </Button>
          </Box>
        </Paper>
      </Box>
      {/* Modal para cambiar contraseña */}
      <Modal open={modalOpen} onClose={handleCloseModal}>
        <Box
          sx={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            bgcolor: "background.paper",
            boxShadow: 24,
            borderRadius: 3,
            p: 4,
            minWidth: 350,
            maxWidth: "90vw",
            display: "flex",
            flexDirection: "column",
            gap: 2,
          }}
        >
          <Typography variant="h6" sx={{ mb: 2 }}>
            Cambiar Contraseña
          </Typography>
          <TextField
            label="Contraseña Actual"
            type={showOld ? "text" : "password"}
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            fullWidth
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={() => setShowOld((v) => !v)} edge="end">
                    {showOld ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          <TextField
            label="Nueva Contraseña"
            type={showNew ? "text" : "password"}
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            fullWidth
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={() => setShowNew((v) => !v)} edge="end">
                    {showNew ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          {msg && (
            <Typography
              color={
                msg.includes("correctamente") ? "success.main" : "error.main"
              }
              sx={{ mt: 1 }}
            >
              {msg}
            </Typography>
          )}
          <Box
            sx={{ display: "flex", justifyContent: "flex-end", gap: 2, mt: 2 }}
          >
            <Button onClick={handleCloseModal} disabled={loading}>
              Cancelar
            </Button>
            <Button
              variant="contained"
              onClick={handleChangePassword}
              disabled={loading || !oldPassword || !newPassword}
            >
              Cambiar
            </Button>
          </Box>
        </Box>
      </Modal>

      <Footer />
    </Box>
  );
}
