import React, { useState } from "react";
import {
  Modal,
  Box,
  Typography,
  TextField,
  InputAdornment,
  IconButton,
  Button,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";

interface BtnCerrarSesionProps {
  open: boolean;
  onClose: () => void;
  onChangePassword: (oldPassword: string, newPassword: string) => Promise<void>;
  loading?: boolean;
  msg?: string | null;
}

export const BtnCerrarSesion: React.FC<BtnCerrarSesionProps> = ({
  open,
  onClose,
  onChangePassword,
  loading = false,
  msg = null,
}) => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showOld, setShowOld] = useState(false);
  const [showNew, setShowNew] = useState(false);

  const handleChangePassword = async () => {
    await onChangePassword(oldPassword, newPassword);
    setOldPassword("");
    setNewPassword("");
  };

  return (
    <Modal open={open} onClose={onClose}>
      <Box
        sx={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          bgcolor: "background.paper",
          boxShadow: 24,
          borderRadius: 3,
          p: 4,
          minWidth: 350,
          maxWidth: "90vw",
          display: "flex",
          flexDirection: "column",
          gap: 2,
        }}
      >
        <Typography variant="h6" sx={{ mb: 2 }}>
          Cambiar Contraseña
        </Typography>
        <TextField
          label="Contraseña Actual"
          type={showOld ? "text" : "password"}
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
          fullWidth
          autoComplete="current-password"
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  onClick={() => setShowOld((v) => !v)}
                  edge="end"
                  aria-label={showOld ? "Ocultar contraseña" : "Mostrar contraseña"}
                >
                  {showOld ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            ),
          }}
          sx={{ mb: 1 }}
        />
        <TextField
          label="Nueva Contraseña"
          type={showNew ? "text" : "password"}
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          fullWidth
          autoComplete="new-password"
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  onClick={() => setShowNew((v) => !v)}
                  edge="end"
                  aria-label={showNew ? "Ocultar contraseña" : "Mostrar contraseña"}
                >
                  {showNew ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
        {msg && (
          <Typography
            color={msg.includes("correctamente") ? "success.main" : "error.main"}
            sx={{ mt: 1 }}
          >
            {msg}
          </Typography>
        )}
        <Box sx={{ display: "flex", justifyContent: "flex-end", gap: 2, mt: 2 }}>
          <Button onClick={onClose} disabled={loading}>
            Cancelar
          </Button>
          <Button
            variant="contained"
            onClick={handleChangePassword}
            disabled={loading || !oldPassword || !newPassword}
          >
            Cambiar
          </Button>
        </Box>
      </Box>
    </Modal>
  );
};
